import { useRef, createContext, useContext } from "react";
import { useViewport } from "../../utils/ViewportProvider";
import Messages from "./Messaging";

const mainWidthContext = createContext();

export function useMainWidth() {
  return useContext(mainWidthContext);
}

export default function MainContent() {
  // Container ref is used to give refernce of width to the
  // Chat input so that is always is 100% width of its parent
  const containerRef = useRef();

  const { show } = useViewport();
  const mainContentWidth = `${containerRef?.current?.offsetWidth}px`;

  return (
    <div className={show.mainContent ? "show" : "hide"} ref={containerRef}>
      <mainWidthContext.Provider value={mainContentWidth}>
        <Messages containerRef={containerRef} />
      </mainWidthContext.Provider>
    </div>
  );
}
