import React from "react";

export default function MessageContextMenu({ show, position }) {
  const { x, y } = position;

  const style = { top: y, left: x };

  return (
    <nav id="contextMenu" className={show ? "show" : "hide"}>
      <button id="deleteMessageButton">Delete Message</button>
    </nav>
  );
}
