import React from "react";
import useConversations from "../utils/ConvorsationProvider";

export default function Message({ i }) {
  const { messageState, userState } = useConversations();
  const [messages, setMessages] = messageState;
  const [user, setUser] = userState;

  //! Take this and turn it into an independant messages component.
  //! now that you have the context set up you don't need to pass messages as props
  console.log(messages);

  return (
    <div>
      <div
        className={`my-1 d-flex flex-column ${
          message.sender_id === user._id
            ? "align-self-end align-items-end"
            : "align-items-start"
        }`}
      >
        <div
          className={`rounded px-2 py-1 ${
            message.sender_id === user._id ? "bg-success text-white" : "border"
          }`}
        >
          {message.content}
        </div>
        <div
          className={`text-muted small ${
            message.sender_id === user._id ? "text-right" : ""
          }`}
        >
          {message.sender_id === user._id &&
          messages[i - 1].sender_id === user._id
            ? "You"
            : message.senderName}
        </div>
      </div>
    </div>
  );
}
