import { useState, useEffect, useMemo } from "react";
import useViewport from "./useViewport";

const useMobileLayout = () => {
  const width = useViewport();

  const initialShow = useMemo(() => {
    return { convos: true, messages: !(width < 575) };
  }, [width]);
  const [show, setShow] = useState(initialShow);

  function toggleMobileView() {
    setShow({
      convos: !show.convos,
      messages: !show.messages,
    });
  }

  useEffect(() => {
    if (width < 575 && show.convos && show.messsages)
      setShow({
        convos: true,
        messages: false,
      });
    return () => setShow(initialShow);
  }, [width, show, initialShow]);

  return [show, toggleMobileView];
};

export default useMobileLayout;
