import React, { useRef, useState, useEffect, useCallback } from "react";
import { Navbar, Container, Row, Col, Spinner } from "react-bootstrap";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";
import { useConversations } from "../utils/ConversationProvider";
import useViewport from "../utils/useViewport";
import MessageContextMenu from "./MessageContextMenu";
import SingleMessage from "./SingleMessage";

export default function Messages({ messages, show, setShow }) {
  const { sendMessage } = useConversations();
  const [contextMenu, setContextMenu] = useState({
    x: "0px",
    y: "0px",
  });
  const [contextMenuShow, setContextMenuShow] = useState(false);

  const bottomRef = useRef();
  function scrollToBottom() {
    bottomRef.current?.scrollIntoView();
  }
  useEffect(() => {
    scrollToBottom();
  }, []);

  const textRef = useRef();
  const width = useViewport();

  function handleRightClick(event, element) {
    if (contextMenuShow) return;
    event.preventDefault();
    // const messageIndex = element.getAttribute("data-key");

    setContextMenu({
      x: `${event.pageX - 75}px`,
      y: `${event.pageY}px`,
    });
    setContextMenuShow(true);
  }

  const dismissContextMenu = useCallback(() => {
    if (!contextMenuShow) return;
    setContextMenuShow(false);
  }, [contextMenuShow]);

  useEffect(() => {
    document.addEventListener("click", dismissContextMenu);
    return () => {
      document.removeEventListener("click", dismissContextMenu);
    };
  }, [dismissContextMenu]);

  return (
    <div className={show ? "show" : "hide"}>
      <MessageContextMenu position={contextMenu} show={contextMenuShow} />

      <div id="messageWrapper">
        {width < 575 ? (
          <Navbar>
            <button
              id="backButton"
              onClick={() => {
                setShow({
                  convos: true,
                  messages: false,
                });
              }}
            >
              <FaArrowLeft className="bg-danger" />
            </button>
          </Navbar>
        ) : null}

        <div className="d-flex flex-column flex-grow-1" id="messages">
          <div className="flex-grow-1 overflow-auto">
            <div className="d-flex flex-column align-items-start justify-content-end px-3">
              {messages.length < 0 ? (
                <Spinner id="spinner" animation="border" />
              ) : (
                messages.map((message, i) => {
                  return (
                    <SingleMessage
                      key={i}
                      index={i}
                      data={[message, messages]}
                      handleRightClick={handleRightClick}
                    />
                  );
                })
              )}
              <div ref={bottomRef}></div>
            </div>
          </div>
        </div>
        <Container fluid id="chatBox">
          <Row>
            <Col xs={7}>
              <textarea ref={textRef} rows="1" id="chatInput" type="text" />
            </Col>
            <Col xs={2}>
              <button
                id="sendButton"
                onClick={(e) => {
                  e.preventDefault();
                  sendMessage(textRef.current.value);
                  textRef.current.value = "";
                }}
              >
                <FaArrowRight className="bg-primary" />
              </button>
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
}
