import { useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useViewportContext } from "../utils/ViewportProvider";
import Conversations from "./SideBar/Conversations";
import Messages from "./Messages";

export default function Dashboard() {
  const { mobileScreen, width, show, setShow } = useViewportContext();
  const { convos, messages } = show;

  useEffect(() => {
    if (width >= 575 && (!convos || !messages)) {
      console.log("big if");
      setShow({
        convos: true,
        messages: true,
      });
      return;
    }
    if (width < 575 && convos && messages) {
      console.log("little if");
      setShow({
        convos: true,
        messages: false,
      });
      return;
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [width]);

  return (
    <>
      {!mobileScreen ? (
        <Container fluid>
          <Row style={{ marginRight: "0px !important" }}>
            <Col sm={4} style={{ paddingRight: "0px" }}>
              <Conversations />
            </Col>
            <Col sm={8} id="messageBox">
              <Messages />
            </Col>
          </Row>
        </Container>
      ) : (
        <Container fluid>
          <Conversations />
          <Messages />
        </Container>
      )}
    </>
  );
}
