import React from "react";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx } from "@emotion/react";
import PropTypes from "prop-types";

import {
  CometChatMessageActions,
  CometChatThreadedMessageReplyCount,
  CometChatReadReceipt,
} from "../";
import { CometChatMessageReactions } from "../Extensions";

import { CometChatContext } from "../../../util/CometChatContext";
import { checkMessageForExtensionsData } from "../../../util/common";
import * as enums from "../../../util/enums.js";

import { theme } from "../../../resources/theme";
import Translator from "../../../resources/localization/translator";

import {
  messageContainerStyle,
  messageWrapperStyle,
  messageTxtWrapperStyle,
  messageTxtContainerStyle,
  messageTxtStyle,
  messageBtnStyle,
  messageInfoWrapperStyle,
  messageReactionsWrapperStyle,
  iconStyle,
} from "./style";

import callIcon from "./resources/video-call.svg";

class CometChatSenderDirectCallBubble extends React.Component {
  messageFrom = "sender";
  static contextType = CometChatContext;

  constructor(props) {
    super(props);
    const message = Object.assign({}, props.message, {
      messageFrom: this.messageFrom,
    });

    this.state = {
      message: message,
      isHovering: false,
    };
  }

  componentDidUpdate(prevProps) {
    const previousMessageStr = JSON.stringify(prevProps.message);
    const currentMessageStr = JSON.stringify(this.props.message);

    if (previousMessageStr !== currentMessageStr) {
      const message = Object.assign({}, this.props.message, {
        messageFrom: this.messageFrom,
      });
      this.setState({ message: message });
    }
  }

  handleMouseHover = () => {
    this.setState(this.toggleHoverState);
  };

  toggleHoverState = (state) => {
    return {
      isHovering: !state.isHovering,
    };
  };

  render() {
    let messageReactions = null;
    const reactionsData = checkMessageForExtensionsData(
      this.state.message,
      "reactions"
    );
    if (reactionsData) {
      if (Object.keys(reactionsData).length) {
        messageReactions = (
          <div
            css={messageReactionsWrapperStyle()}
            className="message__reaction__wrapper"
          >
            <CometChatMessageReactions
              {...this.props}
              message={this.state.message}
              reaction={reactionsData}
            />
          </div>
        );
      }
    }

    let toolTipView = null;
    if (this.state.isHovering) {
      toolTipView = (
        <CometChatMessageActions {...this.props} message={this.state.message} />
      );
    }

    let callMessage = null;
    const joinCallMessage = Translator.translate(
      "YOU_ALREADY_ONGOING_CALL",
      this.props.lang
    );

    if (
      this.context.checkIfDirectCallIsOngoing() ===
      enums.CONSTANTS.CALLS["ONGOING_CALL_SAME_GROUP"]
    ) {
      //ongoing call in same group
      callMessage = (
        <li title={joinCallMessage}>
          <p>{Translator.translate("JOIN", this.props.lang)}</p>
        </li>
      );
    } else if (
      this.context.checkIfDirectCallIsOngoing() ===
      enums.CONSTANTS.CALLS["ONGOING_CALL_DIFF_GROUP"]
    ) {
      //ongoing call in different group

      callMessage = (
        <li title={joinCallMessage}>
          <p>{Translator.translate("JOIN", this.props.lang)}</p>
        </li>
      );
    } else if (this.context.checkIfCallIsOngoing()) {
      //ongoing call

      callMessage = (
        <li title={joinCallMessage}>
          <p>{Translator.translate("JOIN", this.props.lang)}</p>
        </li>
      );
    } else {
      callMessage = (
        <li
          onClick={() =>
            this.props.actionGenerated(
              enums.ACTIONS["JOIN_DIRECT_CALL"],
              this.state.message
            )
          }
        >
          <p>{Translator.translate("JOIN", this.props.lang)}</p>
        </li>
      );
    }

    const messageTitle = Translator.translate(
      "YOU_INITIATED_GROUP_CALL",
      this.props.lang
    );
    return (
      <div
        css={messageContainerStyle()}
        className="sender__message__container message__directcall"
        onMouseEnter={this.handleMouseHover}
        onMouseLeave={this.handleMouseHover}
      >
        {toolTipView}

        <div css={messageWrapperStyle()} className="message__wrapper">
          <div
            css={messageTxtWrapperStyle(this.context)}
            className="message__directcall__wrapper"
          >
            <div
              css={messageTxtContainerStyle()}
              className="message__directcall__container"
            >
              <i
                css={iconStyle(callIcon, this.context)}
                alt={Translator.translate("VIDEO_CALL", this.props.lang)}
              ></i>
              <p css={messageTxtStyle()} className="directcall__title">
                {messageTitle}
              </p>
            </div>
            <ul
              css={messageBtnStyle(this.context)}
              className="directcall__button"
            >
              {callMessage}
            </ul>
          </div>
        </div>

        {messageReactions}

        <div css={messageInfoWrapperStyle()} className="message__info__wrapper">
          <CometChatThreadedMessageReplyCount
            {...this.props}
            message={this.state.message}
          />
          <CometChatReadReceipt {...this.props} message={this.state.message} />
        </div>
      </div>
    );
  }
}

// Specifies the default values for props:
CometChatSenderDirectCallBubble.defaultProps = {
  lang: Translator.getDefaultLanguage(),
  theme: theme,
  message: {},
  loggedInUser: {},
};

CometChatSenderDirectCallBubble.propTypes = {
  lang: PropTypes.string,
  theme: PropTypes.object,
  message: PropTypes.object,
  loggedInUser: PropTypes.object,
};

export { CometChatSenderDirectCallBubble };
