import { useState, useEffect } from "react";
import { Row, Col } from "react-bootstrap";
import { BiConversation, BiGroup } from "react-icons/bi";
import { FiSettings } from "react-icons/fi";
import { useViewport } from "../../utils/ViewportProvider";
import "./menubar.css";

export default function MenuBar({ containerRef }) {
  const [menubarWidth, setMenubarWidth] = useState("100%");
  const { width } = useViewport();

  useEffect(() => {
    if (width >= 575) setMenubarWidth(`${containerRef.current.offsetWidth}px`);
    else setMenubarWidth("100%");
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [width]);

  return (
    <div id="menuBar" style={{ width: menubarWidth }}>
      <Row>
        <Col xs={4}>
          <BiConversation className="menuIcons" />
        </Col>
        <Col xs={4}>
          <BiGroup className="menuIcons" />
        </Col>
        <Col xs={4}>
          <FiSettings className="menuIcons" />
        </Col>
      </Row>
    </div>
  );
}
