import { useState, useEffect } from "react";
import { Nav } from "react-bootstrap";
import { BiConversation, BiGroup } from "react-icons/bi";
import { FiSettings } from "react-icons/fi";
import { useViewport } from "../../utils/ViewportProvider";
import "./menubar.css";

export default function MenuBar({ containerRef, setActiveMenu }) {
  const [menubarWidth, setMenubarWidth] = useState("100%");
  const { width } = useViewport();

  function handleMenuChange(event) {
    event.preventDefault;
    console.log(event);
  }

  useEffect(() => {
    if (width >= 575) setMenubarWidth(`${containerRef.current.offsetWidth}px`);
    else setMenubarWidth("100%");
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [width]);

  return (
    <div id="menuBar" style={{ width: menubarWidth }}>
      <Nav className="justify-content-center">
        <Nav.Item>
          <Nav.Link id="conversations">
            <BiConversation className="menuIcons" />
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link>
            <BiGroup className="menuIcons" />
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link>
            <FiSettings className="menuIcons" />
          </Nav.Link>
        </Nav.Item>
      </Nav>
    </div>
  );
}
